var link=document.querySelector(".button-write-us"),popup=document.querySelector(".write-us"),close=document.querySelector(".write-us-close"),map=document.querySelector(".map-button"),popupMap=document.querySelector(".show-map"),closeMap=document.querySelector(".map-close");link.addEventListener("click",function(e){e.preventDefault(),popup.classList.add("write-us-show")}),close.addEventListener("click",function(e){e.preventDefault(),popup.classList.remove("write-us-show")}),map.addEventListener("click",function(e){e.preventDefault(),popupMap.classList.add("show-map-open")}),closeMap.addEventListener("click",function(e){e.preventDefault(),popupMap.classList.remove("show-map-open")});